// n8n Code node
return $input.all().flatMap((item) => {
  const entry = item.json ?? {};

  // Lookups
  const usersById = Object.fromEntries((entry.includes?.users ?? []).map(u => [u.id, u]));
  const tweetsById = Object.fromEntries((entry.includes?.tweets ?? []).map(t => [t.id, t]));
  const mediaByKey = Object.fromEntries((entry.includes?.media ?? []).map(m => [m.media_key, m]));

  // Map each tweet -> one output item
  return (entry.data ?? []).map((t) => {
    const user = usersById[t.author_id] ?? {};

    // Resolve media
    const mediaKeys = t.attachments?.media_keys ?? [];
    const media = mediaKeys
      .map((k) => {
        const m = mediaByKey[k];
        if (!m) return null;

        if (m.type === "video") {
          const mp4s = (m.variants ?? []).filter(v => v.content_type === "video/mp4");
          const best = mp4s.sort((a, b) => (b.bit_rate ?? 0) - (a.bit_rate ?? 0))[0] ?? null;
          return {
            type: "video",
            url: best?.url ?? null,
            bit_rate: best?.bit_rate ?? null,
            preview_image_url: m.preview_image_url ?? null,
            height: m.height ?? null,
            width: m.width ?? null,
            view_count: m.public_metrics?.view_count ?? null,
          };
        }

        // photo/gif or others
        return {
          type: m.type,
          url: m.url ?? null,
          preview_image_url: m.preview_image_url ?? null,
          height: m.height ?? null,
          width: m.width ?? null,
        };
      })
      .filter(Boolean);

    // Resolve referenced tweets (e.g., replied_to)
    const referenced = (t.referenced_tweets ?? []).map((ref) => {
      const rt = tweetsById[ref.id];
      const rauthor = rt ? usersById[rt.author_id] : undefined;
      return {
        type: ref.type,
        id: ref.id,
        text: rt?.text ?? null,
        author: rt
          ? {
              id: rauthor?.id ?? null,
              username: rauthor?.username ?? null,
              name: rauthor?.name ?? null,
              verified: rauthor?.verified ?? false,
            }
          : null,
      };
    });

    return {
      json: {
        id: t.id,
        text: t.text,
        created_at: t.created_at,
        conversation_id: t.conversation_id ?? null,
        possibly_sensitive: t.possibly_sensitive ?? false,
        metrics: t.public_metrics ?? {},
        hashtags: t.entities?.hashtags?.map((h) => h.tag) ?? [],
        urls: t.entities?.urls ?? [],
        author: {
          id: user.id ?? null,
          username: user.username ?? null,
          name: user.name ?? null,
          verified: user.verified ?? false,
          followers: user.public_metrics?.followers_count ?? 0,
          following: user.public_metrics?.following_count ?? 0,
          tweet_count: user.public_metrics?.tweet_count ?? 0,
          like_count: user.public_metrics?.like_count ?? 0,
          account_created_at: user.created_at ?? null,
        },
        media,
        referenced_tweets: referenced,
      },
    };
  });
});

